mapper.insert:
INSERT INTO article_master (
	user_id
  , type
  , image_id
  , content
  , ingredient
) values (
	#{userId}
  , #{type}
  , #{imageId}
  , #{content}
  , #{ingredient}
)

INSERT INTO article_detail (
	article_id
  , `order`
  , image_id
  , content
) values (
	#{articleId}
  , #{order}
  , #{imageId}
  , #{content}
)

mapper.update:
UPDATE article_master
   SET user_id     = #{userId}
     , image_id    = #{imageId}
     , content     = #{content}
     , ingredient  = #{ingredient}
     , mdfc_dttm   = now()
 WHERE id          = #{id}

UPDATE article_master
   SET use_yn = 'N'
 WHERE id = #{id}
