mapper.insert:
INSERT INTO user(
	user_id,
	name,
	email,
	password
)

VALUES (
	#{userId},
	#{name},
	#{email},
	#{password}
)
;

mapper.update:
UPDATE user
user_id = #{userId}, name = #{name}, email = #{email}, password = #{password}, intro = #{intro}, feed_type = #{feedType}, mdfc_dttm = now()
WHERE user_id = #{userId} AND use_yn = 'Y' ;

UPDATE user
SET
	use_yn = 'N'
WHERE user_id = #{userId}
;
